j_304_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'facilityManagerName': 'j_308',
        'buildingId': 'j_309',
        'streetAddress': 'j_310',
        'city': 'j_311',
        'state': 'j_312',
        'zipCode': 'j_313',
        'closestcity': 'j_314',
        'buildingType': 'j_316',
        'waterStressRegion': 'j_315',
        'numOfTenants': 'j_317',
        'sqFootage': 'j_318',
        'nextButton': 'j_319'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    Appery.CurrentScreen = 'j_304';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */
    j_304_beforeshow = function() {
        Appery.CurrentScreen = 'j_304';
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }
    // screen onload
    screen_368D_onLoad = j_304_onLoad = function() {
        screen_368D_elementsExtraJS();
        $('div[dsid="mobilecontainer"]').css('background-image', 'url("http://i41.tinypic.com/2e325va.png")');

        j_304_deviceEvents();
        j_304_windowEvents();
        screen_368D_elementsEvents();
    }

    // screen window events
    screen_368D_windowEvents = j_304_windowEvents = function() {
        $('#j_304').bind('pageshow orientationchange', function() {
            adjustContentHeightWithPadding();
        });

    }

    // device events
    j_304_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_368D_elementsExtraJS = j_304_elementsExtraJS = function() {
        // screen (screen-368D) extra code

    }

    // screen elements handler
    screen_368D_elementsEvents = j_304_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#j_307 [name="nextButton"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    var facilityManagerName = Appery('facilityManagerName').val();
                    var buildingId = Appery('buildingId').val();
                    var streetAddress = Appery('streetAddress').val();
                    var city = Appery('city').val();
                    var state = Appery('state').val();
                    var zipCode = Appery('zipCode').val();
                    var closestCity = Appery('closestCity').val();
                    var buildingType = Appery('buildingType').val();
                    var waterStressRegion = Appery('waterStressRegion').val();
                    var numOfTenants = Appery('numOfTenants').val();
                    var sqFootage = Appery('sqFootage').val();

                    localStorage.setItem('facilityManagerName', facilityManagerName);
                    localStorage.setItem('buildingId', buildingId);
                    localStorage.setItem('streetAddress', streetAddress);
                    localStorage.setItem('city', city);
                    localStorage.setItem('state', state);
                    localStorage.setItem('zipCode', zipCode);
                    localStorage.setItem('closestCity', closestCity);
                    localStorage.setItem('buildingType', buildingType);
                    localStorage.setItem('waterStressRegion', waterStressRegion);
                    localStorage.setItem('numOfTenants', numOfTenants);
                    localStorage.setItem('sqFootage', sqFootage);

                    //alert(localStorage.getItem('buildingInfoText'));
                    Appery.navigateTo('scWaterConsumption', {
                        reverse: false
                    });

                }
            },
        });

    }

    $("#j_304").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        j_304_beforeshow();
    });

    if (runBeforeShow) {
        j_304_beforeshow();
    } else {
        j_304_onLoad();
    }

}

