j_322_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilegrid_2': 'j_326',
        'mobilegridcell_3': 'j_327',
        'mobilelabel_9': 'j_328',
        'mobilegridcell_4': 'j_329',
        'waterAudit': 'j_330',
        'mobileselectmenuitem_8': 'j_331',
        'mobilegridcell_5': 'j_332',
        'mobilelabel_10': 'j_333',
        'mobilegridcell_6': 'j_334',
        'projectsFromAudit': 'j_335',
        'mobileselectmenuitem_12': 'j_336',
        'mobilebutton_13': 'j_337'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    Appery.CurrentScreen = 'j_322';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */
    j_322_beforeshow = function() {
        Appery.CurrentScreen = 'j_322';
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }
    // screen onload
    screen_173B_onLoad = j_322_onLoad = function() {
        screen_173B_elementsExtraJS();
        $('div[dsid="mobilecontainer"]').css('background-image', 'url("http://i41.tinypic.com/2e325va.png")');

        j_322_deviceEvents();
        j_322_windowEvents();
        screen_173B_elementsEvents();
    }

    // screen window events
    screen_173B_windowEvents = j_322_windowEvents = function() {
        $('#j_322').bind('pageshow orientationchange', function() {
            adjustContentHeightWithPadding();
        });

    }

    // device events
    j_322_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_173B_elementsExtraJS = j_322_elementsExtraJS = function() {
        // screen (screen-173B) extra code

        /* waterAudit */

        $("#j_330").parent().find("a.ui-btn").attr("tabindex", "1");

        /* projectsFromAudit */

        $("#j_335").parent().find("a.ui-btn").attr("tabindex", "2");

    }

    // screen elements handler
    screen_173B_elementsEvents = j_322_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#j_325 [name="mobilebutton_13"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    var scorecardWaterAudit = parseFloat(Appery('waterAudit').val());
                    scorecardWaterAudit = (scorecardWaterAudit * 0.15) / 15;

                    var scorecardWaterProjects = parseFloat(Appery('projectsFromAudit').val());
                    scorecardWaterProjects = (scorecardWaterProjects * 0.10) / 10;

                    localStorage.setItem('scorecardWaterAudit', scorecardWaterAudit);
                    localStorage.setItem('scorecardWaterProjects', scorecardWaterProjects);
                    Appery.navigateTo('scFloors', {
                        reverse: false
                    });

                }
            },
        });

    }

    $("#j_322").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        j_322_beforeshow();
    });

    if (runBeforeShow) {
        j_322_beforeshow();
    } else {
        j_322_onLoad();
    }

}

