        localStorage.setItem('scorecardWaterProjects', 0.5);
        localStorage.setItem('scorecardFaucetFixture', 0.5);
        localStorage.setItem('scorecardLandscape', 0.5);
        localStorage.setItem('scorecardGoals', 0.5);
        localStorage.setItem('scorecardSewerCredits', 0.5);
        localStorage.setItem('scorecardReuse', 0.5);
        localStorage.setItem('scorecardRebates', 0.5);
        localStorage.setItem('scorecardInnovation', 0.5);
        //var scCT = localStorage.getItem('scorecardCoolingTower');

        j_130_deviceEvents();
        j_130_windowEvents();
        screen_3671_elementsEvents();
